/*
 * This is the primary high-level API for the project. In this folder there should be:
 * A class called InsightFacade, this should be in a file called InsightFacade.ts.
 * You should not change this interface at all or the test suite will not work.
 */

export enum InsightDatasetKind {
	Sections = "sections",
	Rooms = "rooms",
}

export interface InsightDataset {
	id: string;
	kind: InsightDatasetKind;
	numRows: number;
}

export interface InsightResult {
	[key: string]: string | number;
}

export class InsightError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, InsightError);
	}
}

export class NotFoundError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, NotFoundError);
	}
}

export class ResultTooLargeError extends Error {
	constructor(message?: string) {
		super(message);
		Error.captureStackTrace(this, ResultTooLargeError);
	}
}

export interface IInsightFacade {
	/**
	 * Add a dataset to insightUBC.
	 *
	 * @param id  The id of the dataset being added. Follows the format /^[^_]+$/
	 * @param content  The base64 content of the dataset. This content should be in the form of a serialized zip file.
	 * @param kind  The kind of the dataset
	 *
	 * @return Promise <string[]>
	 *
	 * The promise should fulfill on a successful add, reject for any failures.
	 * The promise should fulfill with a string array,
	 * containing the ids of all currently added datasets upon a successful add.
	 * The promise should reject with an InsightError describing the error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 * If id is the same as the id of an already added dataset, the dataset should be rejected and not saved.
	 *
	 * After receiving the dataset, it should be processed into a data structure of
	 * your design. The processed data structure should be persisted to disk; your
	 * system should be able to load this persisted value into memory for answering
	 * queries.
	 *
	 * Ultimately, a dataset must be added or loaded from disk before queries can
	 * be successfully answered.
	 */
	addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]>;

	/**
	 * Remove a dataset from insightUBC.
	 *
	 * @param id  The id of the dataset to remove. Follows the format /^[^_]+$/
	 *
	 * @return Promise <string>
	 *
	 * The promise should fulfill upon a successful removal, reject on any error.
	 * Attempting to remove a dataset that hasn't been added yet counts as an error.
	 *
	 * An id is invalid if it contains an underscore, or is only whitespace characters.
	 *
	 * The promise should fulfill the id of the dataset that was removed.
	 * The promise should reject with a NotFoundError (if a valid id was not yet added)
	 * or an InsightError (invalid id or any other source of failure) describing the error.
	 *
	 * This will delete both disk and memory caches for the dataset for the id meaning
	 * that subsequent queries for that id should fail unless a new addDataset happens first.
	 */
	removeDataset(id: string): Promise<string>;

	/**
	 * Perform a query on insightUBC.
	 *
	 * @param query  The query to be performed.
	 *
	 * If a query is incorrectly formatted, references a dataset not added (in memory or on disk),
	 * or references multiple datasets, it should be rejected.
	 *
	 * @return Promise <InsightResult[]>
	 *
	 * The promise should fulfill with an array of results.
	 * The promise should reject with a ResultTooLargeError (if the query returns too many results)
	 * or an InsightError (for any other source of failure) describing the error.
	 */
	performQuery(query: unknown): Promise<InsightResult[]>;

	/**
	 * List all currently added datasets, their types, and number of rows.
	 *
	 * @return Promise <InsightDataset[]>
	 * The promise should fulfill an array of currently added InsightDatasets, and will only fulfill.
	 */
	listDatasets(): Promise<InsightDataset[]>;
}

import {
	IInsightFacade,
	InsightDataset,
	InsightDatasetKind,
	InsightError,
	InsightResult,
	NotFoundError,
} from "./IInsightFacade";

import {Database} from "../model/Database";
import * as fs from "fs-extra";
import {Query, parseOptions, parseWhere} from "../query/QueryParser";
// import {Query, parseOptions, parseWhere} from "../query/QueryParser";

/**
 * This is the main programmatic entry point for the project.
 * Method documentation is in IInsightFacade
 *
 */
export default class InsightFacade implements IInsightFacade {
	private database: Database;

	constructor() {
		this.database = new Database();
		console.log("InsightFacadeImpl::init()");
	}

	public async addDataset(id: string, content: string, kind: InsightDatasetKind): Promise<string[]> {
		try {
			if (this.database.invalidId(id)) {
				return Promise.reject(new InsightError());
			}

			if (kind === InsightDatasetKind.Rooms) {
				return Promise.reject(new InsightError());
			}

			return this.database.addValidDataset(id, content);
		} catch (err) {
			return Promise.reject(new InsightError());
		}
	}

	public removeDataset(id: string): Promise<string> {
		if (this.database.invalidId(id)) {
			return Promise.reject(new InsightError());
		} else if (!fs.existsSync("./data/" + id)) {
			return Promise.reject(new NotFoundError());
		} else {
			fs.unlinkSync("./data/" + id);
			return Promise.resolve(id);
		}
	}

	public performQuery(query: unknown): Promise<InsightResult[]> {
		try {
			if (query === null) {
				return Promise.reject(new InsightError("Query is null"));
			}

			if (typeof query !== "string") {
				return Promise.reject(new InsightError("Query is not a string"));
			}

			const queryObject: Query = JSON.parse(query);
			// Get name of the dataset
			let datasetId = queryObject.OPTIONS.COLUMNS[0].split("_")[0];
			let result = parseWhere(queryObject.WHERE, this.database.readDataset(datasetId));
			return Promise.resolve(parseOptions(queryObject.OPTIONS, result));
		} catch (error) {
			return Promise.reject(new InsightError());
		}
	}

	// not sure if we're allowed to have this async either
	public listDatasets(): Promise<InsightDataset[]> {
		let insightDatasetLists: InsightDataset[] = [];
		let datasetIds = this.database.getAllIds();

		datasetIds.forEach((datasetId) => {
			let datasetString = fs.readFileSync("./data/" + datasetId).toString();
			let dataset = JSON.parse(datasetString);
			let sum = 0;

			dataset.forEach((course: string) => {
				// course.length = num of sections in each course
				sum += course.length;
			});

			let insightDataset: InsightDataset = {
				id: datasetId,
				kind: InsightDatasetKind.Sections,
				numRows: sum,
			};

			insightDatasetLists.push(insightDataset);
		});

		return Promise.resolve(insightDatasetLists);
	}
}

import * as fs from "fs-extra";
import {Dataset} from "./Dataset";
import {Section} from "./Section";
import {InsightDatasetKind, InsightError, InsightDataset, InsightResult} from "../controller/IInsightFacade";
import * as zip from "jszip";

// list of valid datasets
export class Database {
	constructor() {
		if (!fs.existsSync("./data")) {
			fs.mkdirSync("./data");
		}
	}

	public getAllIds(): string[] {
		return fs.readdirSync("./data");
	}

	public invalidId(id: string): boolean {
		return id === "" || id.includes("_") || id === " ";
	}

	// Create

	// add a valid dataset
	// file name = id of dataset
	// TODO: refactor to not-async fn
	public async addValidDataset(id: string, file: string): Promise<string[]> {
		try {
			let courses = await this.getAllCoursesInZip(file);
			let valid = false;

			for (const course of courses) {
				for (const section of course) {
					let sectionObj = new Section(section);

					if (sectionObj.isValid()) {
						valid = true;
					}
				}
			}

			// if no valid section, return InsightError
			if (!valid) {
				throw new InsightError();
			}

			if (!fs.pathExistsSync("./data/" + id)) {
				fs.writeFileSync("./data/" + id, JSON.stringify(courses));
			} else {
				return Promise.reject(new InsightError());
			}

			return Promise.resolve(this.getAllIds());
		} catch (err) {
			return Promise.reject(new InsightError());
		}
	}

	/**
	 * This function returns all the courses in a zip file
	 * @param file name of the file example "pair.zip"
	 * @returns {Promise<string[]>} sections
	 */
	public async getAllCoursesInZip(file: string): Promise<string[]> {
		try {
			let promises: Array<Promise<string> | undefined> = [];
			let courseNames: string[] = [];
			let sections: string[] = [];

			await zip.loadAsync(file, {base64: true}).then((unzip) => {
				unzip.folder("courses")?.forEach((course) => {
					if (course !== ".DS_Store") {
						promises.push(unzip.folder("courses")?.file(course)?.async("string"));
					}
				});
			});

			return Promise.all(promises).then((arrCourses) => {
				arrCourses.forEach((course) => {
					if (course != null && course !== undefined) {
						let courseResult = JSON.parse(course).result;
						sections.push(courseResult);
					}
				});
				return sections;
			});
		} catch (err) {
			return Promise.reject(new InsightError());
		}
	}

	// Read
	public getAllDatasets(): Dataset[] {
		let datasetIds = this.getAllIds();
		let datasets: Dataset[] = [];

		datasetIds.forEach((datasetId) => {
			let datasetString = fs.readFileSync("./data/" + datasetId).toString();
			let dataset = JSON.parse(datasetString) as any[];
			// Flatten data from all courses to a single array
			datasets.push(new Dataset(datasetId, dataset.flat()));
		});
		return datasets;
	}

	public async readDataset(id: string): Promise<any[]> {
		try {
			let datasetString = fs.readFileSync("./data/" + id).toString();
			let dataset = JSON.parse(datasetString) as any[];
			// Flatten data from all courses to a single array
			return Promise.resolve(dataset.flat());
		} catch (err) {
			return Promise.reject(new InsightError());
		}
	}
}

/**
 * Dataset class
 * @param {string} id id of the dataset
 * @param {Array<{[key: string]: any}>} data data in JSON
 */
export class Dataset {
	public id: string;
	public data: Array<{[key: string]: any}>;

	constructor(id: string, data: Array<{[key: string]: any}>) {
		this.id = id;
		this.data = data;
	}

	public getID(): string {
		return this.id;
	}

	public getData(): Array<{[key: string]: any}> {
		return this.data;
	}
}

import {timeLog} from "console";

type MField = string | undefined;
type SField = number | undefined;

export class Section {
	private readonly id: MField;
	private readonly uuid: MField;
	private readonly title: MField;
	private readonly instructor: MField;
	private readonly dept: MField;
	private readonly year: SField;
	private readonly avg: SField;
	private readonly pass: SField;
	private readonly fail: SField;
	private readonly audit: SField;

	constructor(section: any) {
		this.id = section["id"];
		this.uuid = section["Course"];
		this.title = section["Title"];
		this.instructor = section["Professor"];
		this.dept = section["Subject"];
		this.year = section["Year"];
		this.avg = section["Avg"];
		this.pass = section["Pass"];
		this.fail = section["Fail"];
		this.audit = section["Audit"];
	}

	public isValid(): boolean {
		return (
			this.id !== undefined &&
			this.uuid !== undefined &&
			this.title !== undefined &&
			this.instructor !== undefined &&
			this.dept !== undefined &&
			this.year !== undefined &&
			this.avg !== undefined &&
			this.pass !== undefined &&
			this.fail !== undefined &&
			this.audit !== undefined
		);
	}
}

import {InsightError, ResultTooLargeError} from "../controller/IInsightFacade";
import {Section} from "../model/Section";

export interface Query {
	WHERE: Where;
	OPTIONS: Options;
}

type WhereComparators = "LT" | "GT" | "EQ" | "AND" | "OR" | "IS" | "NOT";

interface Where {
	[key: string]: {[key: string]: Where} | Array<{[key: string]: Where}>;
}

interface Options {
	COLUMNS: string[];
	ORDER?: string;
}

const enableLogging = false;
const recordId = 36004;

/**
 * This parses the 'where' clause
 * @param whereCondition - This is the 'where' clause of the query
 * @param data - Data to run where clause on
 * @returns - Returns all rows which meet the 'where' clause
 */
export async function parseWhere(whereCondition: Where, data: Promise<any[]>): Promise<Section[]> {
	if (whereCondition === null) {
		throw new InsightError("Empty filters");
	}
	// Filter the data as per where condition
	let log = true;
	let d = (await data).filter((item) => {
		if (item.id === recordId && enableLogging) {
			console.log(item);
			console.log("wherecondition", JSON.stringify(whereCondition));
		}
		let ps = Object.keys(whereCondition);
		let result = true;
		for (const p of ps) {
			if (item.id === recordId && enableLogging) {
				console.log("p", p);
			}

			try {
				let parseWhereVal = parseWhereComparators(item, whereCondition, p as WhereComparators);
				result = result && parseWhereVal;
			} catch (err) {
				throw new InsightError("Failed to parse WHERE clause");
			}
		}

		if (enableLogging && log && result) {
			console.log(item);
			log = false;
		}
		return result;
	});
	if (d.length > 5000) {
		throw new ResultTooLargeError("Result too large(>5000)");
	}

	return d;
}

function parseWhereComparators(item: any, whereCondition: Where, comparator: WhereComparators): boolean {
	let result = false;
	switch (comparator.toString()) {
		case "LT":
			Object.keys(whereCondition["LT"]).forEach((key) => {
				result = item[parseWhereField(key) || ""] < (whereCondition["LT"] as any)[key];
			});
			break;
		case "GT":
			Object.keys(whereCondition["GT"]).forEach((key) => {
				result = item[parseWhereField(key) || ""] > (whereCondition["GT"] as any)[key];
			});
			break;
		case "EQ":
			Object.keys(whereCondition["EQ"]).forEach((key) => {
				result = item[parseWhereField(key) || ""].toString() === (whereCondition["EQ"] as any)[key].toString();
			});
			break;
		case "IS":
			Object.keys(whereCondition["IS"]).forEach((key) => {
				let o: any = whereCondition["IS"];
				if (o[key][0] === "*" && o[key][o[key].length - 1] === "*") {
					result = item[parseWhereField(key) || ""].includes(o[key].replaceAll("*", ""));
				} else if (o[key][0] === "*") {
					result = item[parseWhereField(key) || ""].endsWith(o[key].substring(1));
				} else if (o[key][o[key].length - 1] === "*") {
					result = item[parseWhereField(key) || ""].startsWith(o[key].substring(0, o[key].length - 1));
				} else if (o[key].includes("*")) {
					throw new InsightError("Invalid comparator");
				} else {
					result = item[parseWhereField(key) || ""].toString() === o[key].toString();
				}
			});
			break;
		case "NOT":
			Object.keys(whereCondition["EQ"]).forEach((key) => {
				result = item[parseWhereField(key) || ""].toString() !== (whereCondition["EQ"] as any)[key].toString();
			});
			break;
		case "AND":
			result = processAndOR("and", whereCondition["AND"] as [], true, item);
			break;
		case "OR":
			result = processAndOR("or", whereCondition["OR"] as [], result, item);
			break;
		default:
			throw new InsightError("Invalid comparator");
	}
	return result;
}

function processAndOR(comparator: "and" | "or", condition: any[], result: boolean, item: any) {
	let r: boolean = result;
	for (const obj of condition) {
		let s = parseWhereComparators(item, obj, Object.keys(obj)[0] as WhereComparators);
		if (comparator === "and") {
			r = r && s;
		} else {
			r = r || s;
		}
		if (item.id === recordId && enableLogging) {
			console.log(comparator, "-par condition:", Object.keys(obj)[0], JSON.stringify(obj));
			console.log("result of", comparator, JSON.stringify(obj), r);
		}
	}
	if (item.id === recordId && enableLogging) {
		console.log("result of processAndOR", r);
	}
	return r;
}

/**
 * This parses the entire 'OPTIONS' clause of query
 * @param obj
 * @returns
 */
export async function parseOptions(options: Options, data: Promise<any[]>): Promise<any[]> {
	if (options === null) {
		throw new InsightError("Empty options");
	}

	let res = await data.then((d) => {
		// Parsing columns
		let columns = options.COLUMNS;
		let order = options.ORDER;
		let result: Array<{[key: string]: string | number}> = [];
		for (const row of d) {
			let rowResult: {[key: string]: string | number} = {};
			for (const col of columns) {
				if (col.includes("uuid")) {
					rowResult[col] = row[parseWhereField(col) || ""].toString();
				} else if (col.toLowerCase().includes("year")) {
					rowResult[col] = Number.parseInt(row[parseWhereField(col) || "0"], 10);
				} else {
					rowResult[col] = row[parseWhereField(col) || ""];
				}
			}
			result.push(rowResult);
		}

		// parsing order
		if (!order) {
			return result;
		}
		return result.sort((a, b) => (a[order || ""] > b[order || ""] ? 1 : -1));
	});
	return res;
}

function parseWhereField(key: string) {
	if (key.includes("_")) {
		key = key.split("_")[1];
	}
	if (key === "avg") {
		return "Avg";
	}
	if (key === "dept") {
		return "Subject";
	}
	if (key === "id") {
		return "Course";
	}
	if (key === "uuid") {
		return "id";
	}
	if (key === "title") {
		return "Title";
	}
	if (key === "instructor") {
		return "Professor";
	}
	if (key === "year") {
		return "Year";
	}
	if (key === "pass") {
		return "Pass";
	}
	if (key === "audit") {
		return "Audit";
	}
	if (key === "fail") {
		return "Fail";
	}
}
